import React from "react";
import styled from "styled-components";
import "react-slideshow-image/dist/styles.css";
import { Slide } from "react-slideshow-image";

const data = [
  {
    id: 1,
    image:
      "data:image/jpeg;base64,",
  },
  {
    id: 2,
    image:
      "data:image/jpeg;base64,",
  },
  {
    id: 3,
    image:
      "data:image/jpeg;base64,",
  },
];

export const Target = ({ resultRef, tarifRef }) => {
  return (
    <Mains ref={resultRef}>
      <Container>
        <h1>
          <strong>ГЛАВНАЯ ЦЕЛЬ</strong> ПРАКТИКУМА
        </h1>
        <h3>
          СОЗДАТЬ ПРОСТРАНСТВО, В КОТОРОМ РОСТ КАЖДОГО УЧАСТНИКА НЕИЗБЕЖЕН
        </h3>

        <Block>
          <Title>
            <p>Самая актуальная информация по маркетплейсам</p>
            <p>Сильнейшие селлеры в одном месте</p>
            <p>Нетворкинг и новые полезные знакомства</p>
            <p>Возможность участия из любой точки мира</p>
            <p>Подарки и призы от партнеров</p>
          </Title>

          <div className="slide-container  slider">
            <Slide autoplay={true} duration={4000} transitionDuration={500}>
              {data.map((el, index) => (
                <div key={index}>
                  <img
                    src={el.image}
                    alt=""
                    style={{ width: "100%", borderRadius: "30px" }}
                  />
                </div>
              ))}
            </Slide>
          </div>
        </Block>

        <div className="btn">
          <button
            onClick={() =>
              tarifRef.current &&
              tarifRef.current.scrollIntoView({ behavior: "smooth" })
            }
            className="bgc-button"
          >
            УЧАСТВОВАТЬ
          </button>
        </div>
      </Container>
    </Mains>
  );
};

const Mains = styled("div")`
  display: flex;
  justify-content: center;
  margin-top: 10rem;
`;

const Container = styled("div")`
  width: 70rem;
  background-color: white;
  padding: 5rem;
  border-radius: 20px;

  h1 {
    font-weight: 600;
    font-size: 60px;
    strong {
      color: #9400fd;
    }
  }

  h3 {
    font-weight: 600;
    font-size: 25px;
    width: 40rem;
  }

  .btn {
    display: flex;
    justify-content: center;
    margin-top: 10rem;
  }

  @media (max-width: 1145px) {
    width: 60rem;

    h1 {
      font-size: 50px;
    }

    h3 {
      font-size: 20px;
    }
  }

  @media (max-width: 1020px) {
    width: 50rem;
  }

  @media (max-width: 860px) {
    width: 40rem;

    h1 {
      font-size: 40px;
    }

    h3 {
      width: 30rem;
      font-size: 15px;
    }
  }

  @media (max-width: 700px) {
    width: 35rem;
  }

  @media (max-width: 630px) {
    width: 30rem;

    h1 {
      font-size: 30px;
    }

    h3 {
      width: 15rem;
      font-size: 15px;
    }
  }

  @media (max-width: 520px) {
    width: 25rem;
    padding: 2rem;

    h1 {
      font-size: 30px;
    }

    h3 {
      width: 15rem;
      font-size: 15px;
    }

    .btn {
      margin-top: 1rem;
    }
  }

  @media (max-width: 460px) {
    width: 20rem;

    h1 {
      font-size: 19px;
    }

    h3 {
      font-size: 10px;
    }
  }
`;

const Title = styled("div")`
  display: flex;
  flex-direction: column;
  gap: 2rem;
  p {
    font-size: 20px;
    margin: 0;
  }

  @media (max-width: 500px) {
    gap: 1rem;
    p {
      font-size: 13px;
    }
  }
`;

const Block = styled("div")`
  display: flex;
  gap: 2rem;
  margin-top: 5rem;

  .slider {
    width: 30rem;
  }

  @media (max-width: 1145px) {
    display: flex;
    flex-direction: column;
    align-items: center;
  }

  @media (max-width: 630px) {
    .slider {
      width: 25rem;
    }
  }

  @media (max-width: 520px) {
    margin-top: 2rem;

    .slider {
      width: 20rem;
    }
  }

  @media (max-width: 460px) {
    .slider {
      width: 15rem;
    }
  }
`;
